files.content:
html {
  font-size: 100%;
}
body {
  font-family: sans-serif;
  font-size: 1rem;
  background-color: #f5f5f5;
  margin: 1.5rem;
  color: rgba(0, 0, 0, 0.87);
}
.msb-card {
  background-color: white;
  border: 1px solid rgba(0, 0, 0, 0.15);
  border-radius: 12px;
  padding: 1rem;
  width: 17.75rem;
  height: 15.5rem;
  margin-right: 1rem;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}
.msb-card-header {
}
.msb-card-title {
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 0.25rem;
}
.msb-card-subtitle {
  font-size: 1rem;
}
.msb-card-content {
  flex: 1 1 auto;
  display: flex;
  align-items: flex-start;
  align-content: flex-start;
  justify-content: flex-start;
}
.msb-card-actions {
}
.msb-button {
  cursor: pointer;
}

const cardAction = document.querySelector('.msb-button');
cardAction.addEventListener('click', () => {
  console.log('card action clicked');
});
